package com.svlada.entity.parent;import java.io.Serializable;/** * id	自增长	int	唯一 name	分类名称	String code	编码，简码	String	唯一 pid	父ID	Int order1	排序	Int type	类型	String	类型，a:文章目录；p:产品目录 showInNav	是否显示在首页的导航条上	String	y:显示,n:不显示；默认n。 仅对type=p有效 *///@Entity//@Table(name="CatalogParent")public class CatalogParent  implements Serializable {	private static final long serialVersionUID = 1L;	private String id;	private String pid;	private String name;	private String code;	private int order1;	private String type;	private String showInNav;	private String showInNavStr;//是否在导航栏显示的中文化		/**	 * 是否显示在首页的导航条上	 */	public static final String catalog_showInNav_y = "y";	public static final String catalog_showInNav_n = "n";		/**	 * type类型	 */	public static final String catalog_type_p = "p";//商品	public static final String catalog_type_a = "a";//文章	public CatalogParent() {		super();	}	public CatalogParent(String id, String name) {		super();		this.id = id;		this.name = name;	}	public String getPid() {		return pid;	}	public void setPid(String pid) {		this.pid = pid;	}	public String getType() {		return type;	}	public void setType(String type) {		this.type = type;	}	public String getId() {		return id;	}	public void setId(String id) {		this.id = id;	}	public String getName() {		return name;	}	public void setName(String name) {		this.name = name;	}	public int getOrder1() {		return order1;	}	public void setOrder1(int order1) {		this.order1 = order1;	}	public String getCode() {		return code;	}	public void setCode(String code) {		this.code = code;	}	public String getShowInNav() {		return showInNav;	}	public void setShowInNav(String showInNav) {		this.showInNav = showInNav;	}	public String getShowInNavStr() {		return showInNavStr;	}	public void setShowInNavStr(String showInNavStr) {		this.showInNavStr = showInNavStr;	}}