package com.svlada.entity.parent;import com.svlada.entity.product.Product;import java.io.Serializable;/** * *///@Entity//@Table(name="Favorite")public class Favorite implements Serializable {	private static final long serialVersionUID = 1L;	private String id;	private String account;	private String productID;	private String createTime;		private Product product;	public String getId() {		return id;	}	public void setId(String id) {		this.id = id;	}	public String getAccount() {		return account;	}	public void setAccount(String account) {		this.account = account;	}	public String getProductID() {		return productID;	}	public void setProductID(String productID) {		this.productID = productID;	}	public String getCreateTime() {		return createTime;	}	public void setCreateTime(String createTime) {		this.createTime = createTime;	}	public Product getProduct() {		return product;	}	public void setProduct(Product product) {		this.product = product;	}	}